// Extract data from webhook
const webhookData = items[0].json;
const serviceType = webhookData.body.service_type;
const pages = webhookData.body.pages || 1;

// Calculate estimate based on service type and pages
let estimate = 0;
let priceBreakdown = '';

if (serviceType === "web app" || serviceType === "website") {
  // Website/Web App Pricing Logic
  if (pages === 1) {
    estimate = 500; // Single page website (landing page)
    priceBreakdown = "Single page landing site";
  } else if (pages >= 2 && pages <= 5) {
    estimate = 1500 + (pages - 2) * 300; // Base $1500 + $300 per additional page
    priceBreakdown = `Small website: Base $1500 + ${pages - 2} additional pages × $300`;
  } else if (pages >= 6 && pages <= 10) {
    estimate = 2500 + (pages - 6) * 250; // Base $2500 + $250 per additional page
    priceBreakdown = `Medium website: Base $2500 + ${pages - 6} additional pages × $250`;
  } else if (pages > 10 && pages <= 20) {
    estimate = 4000 + (pages - 10) * 200; // Base $4000 + $200 per additional page
    priceBreakdown = `Large website: Base $4000 + ${pages - 10} additional pages × $200`;
  } else {
    estimate = 6000 + (pages - 20) * 150; // Base $6000 + $150 per additional page
    priceBreakdown = `Enterprise website: Base $6000 + ${pages - 20} additional pages × $150`;
  }

} else if (serviceType === "mobile app") {
  // Mobile App Pricing Logic (typically more expensive than websites)
  if (pages <= 3) {
    estimate = 3000; // Basic mobile app with few screens
    priceBreakdown = "Basic mobile app (1-3 screens)";
  } else if (pages >= 4 && pages <= 8) {
    estimate = 5000 + (pages - 4) * 500; // Base $5000 + $500 per additional screen
    priceBreakdown = `Standard mobile app: Base $5000 + ${pages - 4} additional screens × $500`;
  } else if (pages >= 9 && pages <= 15) {
    estimate = 7500 + (pages - 9) * 400; // Base $7500 + $400 per additional screen
    priceBreakdown = `Advanced mobile app: Base $7500 + ${pages - 9} additional screens × $400`;
  } else {
    estimate = 10000 + (pages - 15) * 300; // Base $10000 + $300 per additional screen
    priceBreakdown = `Complex mobile app: Base $10000 + ${pages - 15} additional screens × $300`;
  }

} else {
  // Default/Unknown service type
  estimate = 1000;
  priceBreakdown = "Custom service - requires consultation";
}

// Return in correct n8n format
return [
  {
    json: {
      estimate: estimate,
      service_type: serviceType,
      pages: pages,
      price_breakdown: priceBreakdown,
      details: `Estimated cost for ${serviceType} with ${pages} ${serviceType === "mobile app" ? "screens" : "pages"}`,
      currency: "USD",
      timestamp: new Date().toISOString()
    }
  }
];
